import HomePage from '../pages/home-page';
describe('Home page', () => {
  it("Verify title in homepage", async () => {
    // await browser.url('https://practice.automationbro.com/');
    //open home page
    await HomePage.open();
    await expect(browser).toHaveTitle("Practice E-Commerce Site – Automation Bro");
  });
  it("Verify title in about page", async () => {
    await browser.url('https://practice.automationbro.com/about/');
    await expect(browser).toHaveUrl("https://practice.automationbro.com/about/");
  });
  it("Click on get Started and validate Url", async () => {
    await HomePage.open();
    await $('#get-started').click();
    await expect(browser).toHaveUrlContaining("get-started");
    await $('//*[@id="masthead"]/div/div/div[1]/div/a').click();
    await expect(browser).not.toHaveUrlContaining("get-started");
  });
  it("Find the heading element and assert text", async () => {
    await HomePage.open();
    const headingEl = await $('.elementor-widget-container h1');
    const headingText = await headingEl.getText();
    await expect(headingText).toEqual('Think different. Make different.');
    //Another way is
    await expect(headingEl).toHaveText('Think different. Make different.');
  });
});
//# sourceMappingURL=data:application/json;charset=utf-8;base64,

sourcesContent:
import HomePage from '../pages/home-page';
describe('Home page', () => {
   it("Verify title in homepage", async() => {
      // await browser.url('https://practice.automationbro.com/');
      //open home page
      await HomePage.open()
       await expect(browser).toHaveTitle("Practice E-Commerce Site – Automation Bro");
    })

    it("Verify title in about page", async() => {
        await browser.url('https://practice.automationbro.com/about/');
        await expect(browser).toHaveUrl("https://practice.automationbro.com/about/");
     })

     it("Click on get Started and validate Url", async() => {
        await HomePage.open()
        await $('#get-started').click();
        await expect(browser).toHaveUrlContaining("get-started");
        await $('//*[@id="masthead"]/div/div/div[1]/div/a').click();
        await expect(browser).not.toHaveUrlContaining("get-started");
        
     })

     it("Find the heading element and assert text", async() => {
        await HomePage.open()
        const headingEl = await $('.elementor-widget-container h1'); 
        const headingText = await headingEl.getText();
        await expect(headingText).toEqual('Think different. Make different.');
        //Another way is
        await expect(headingEl).toHaveText('Think different. Make different.');
     })
});

