        const id = await browser.getWindowHandles();
        for(let i=0; i<=id.length;i++){
            if(id[i]!=parentId){
                await browser.switchToWindow(id[i]);
                await browser.maximizeWindow();
                    await iframePage.clickOnSignInIconOnNewWindow.click()  
